import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/objectStorage.ts.content:
import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

// The object storage client is used to interact with the object storage service.
export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

// The object storage service is used to interact with the object storage service.
export class ObjectStorageService {
  constructor() {}

  // Gets the public object search paths.
  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  // Gets the private object directory.
  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  // Gets the upload URL for an object entity.
  async getObjectEntityUploadURL(): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    if (!privateObjectDir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }

    const objectId = randomUUID();
    const fullPath = `${privateObjectDir}/uploads/${objectId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the object entity file from the object path.
  async getObjectEntityFile(objectPath: string): Promise<File> {
    if (!objectPath.startsWith("/objects/")) {
      throw new ObjectNotFoundError();
    }

    const parts = objectPath.slice(1).split("/");
    if (parts.length < 2) {
      throw new ObjectNotFoundError();
    }

    const entityId = parts.slice(1).join("/");
    let entityDir = this.getPrivateObjectDir();
    if (!entityDir.endsWith("/")) {
      entityDir = `${entityDir}/`;
    }
    const objectEntityPath = `${entityDir}${entityId}`;
    const { bucketName, objectName } = parseObjectPath(objectEntityPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const objectFile = bucket.file(objectName);
    const [exists] = await objectFile.exists();
    if (!exists) {
      throw new ObjectNotFoundError();
    }
    return objectFile;
  }

  normalizeObjectEntityPath(rawPath: string): string {
    if (!rawPath.startsWith("https://storage.googleapis.com/")) {
      return rawPath;
    }

    // Extract the path from the URL by removing query parameters and domain
    const url = new URL(rawPath);
    const rawObjectPath = url.pathname;

    let objectEntityDir = this.getPrivateObjectDir();
    if (!objectEntityDir.endsWith("/")) {
      objectEntityDir = `${objectEntityDir}/`;
    }

    if (!rawObjectPath.startsWith(objectEntityDir)) {
      return rawObjectPath;
    }

    // Extract the entity ID from the path
    const entityId = rawObjectPath.slice(objectEntityDir.length);
    return `/objects/${entityId}`;
  }

  // Downloads an object to the response.
  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      // Get file metadata
      const [metadata] = await file.getMetadata();

      // Set appropriate headers
      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `public, max-age=${cacheTtlSec}`,
      });

      // Stream the file to the response
      const stream = file.createReadStream();

      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { openaiService } from "./services/openai";
import { financialDataService } from "./services/financial-data";
import { newsService } from "./services/news";
import { 
  insertStockSchema, 
  insertStockAnalysisSchema, 
  insertDebateSchema, 
  insertPortfolioPositionSchema,
  insertChatConversationSchema,
  insertChatMessageSchema,
  insertPortfolioUploadSchema
} from "@shared/schema";
import { ObjectStorageService, ObjectNotFoundError } from "./objectStorage";

export async function registerRoutes(app: Express): Promise<Server> {
  // Personas endpoints
  app.get("/api/personas", async (req, res) => {
    try {
      const personas = await storage.getPersonas();
      res.json(personas);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch personas" });
    }
  });

  // Stocks endpoints
  app.get("/api/stocks", async (req, res) => {
    try {
      const stocks = await storage.getStocks();
      res.json(stocks);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch stocks" });
    }
  });

  app.get("/api/stocks/search", async (req, res) => {
    try {
      const { q } = req.query;
      if (!q || typeof q !== 'string') {
        return res.status(400).json({ message: "Search query required" });
      }

      const results = await financialDataService.searchStocks(q);
      res.json(results);
    } catch (error) {
      res.status(500).json({ message: "Failed to search stocks" });
    }
  });

  app.get("/api/stocks/:symbol", async (req, res) => {
    try {
      const { symbol } = req.params;

      // Try to get from storage first
      let stock = await storage.getStockBySymbol(symbol.toUpperCase());

      if (!stock) {
        // Fetch from external API and create
        const stockData = await financialDataService.getStockQuote(symbol);
        const financialMetrics = await financialDataService.getFinancialMetrics(symbol).catch(() => null);

        stock = await storage.createStock({
          symbol: stockData.symbol,
          name: stockData.name,
          currentPrice: stockData.price.toString(),
          priceChange: stockData.change.toString(),
          priceChangePercent: stockData.changePercent.toString(),
          marketData: {
            ...stockData,
            ...(financialMetrics ? { financialMetrics } : {})
          }
        });
      } else {
        // Update with fresh data
        const stockData = await financialDataService.getStockQuote(symbol);
        stock = await storage.updateStock(stock.id, {
          currentPrice: stockData.price.toString(),
          priceChange: stockData.change.toString(),
          priceChangePercent: stockData.changePercent.toString(),
          marketData: stockData
        });
      }

      res.json(stock);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch stock data" });
    }
  });

  // Stock analysis endpoints
  app.post("/api/stocks/:symbol/analyze", async (req, res) => {
    try {
      const { symbol } = req.params;

      // Get or create stock
      let stock = await storage.getStockBySymbol(symbol.toUpperCase());
      if (!stock) {
        const stockData = await financialDataService.getStockQuote(symbol);
        stock = await storage.createStock({
          symbol: stockData.symbol,
          name: stockData.name,
          currentPrice: stockData.price.toString(),
          priceChange: stockData.change.toString(),
          priceChangePercent: stockData.changePercent.toString(),
          marketData: { ...stockData }
        });
      }

      // Get personas and analyze
      const personas = await storage.getPersonas();
      const newsArticles = await newsService.getStockNews(symbol);
      const newsContext = newsArticles.map(article => article.title);

      const analyses = [];
      for (const persona of personas) {
        try {
          const analysis = await openaiService.analyzeStockByPersona(
            persona,
            stock,
            stock.marketData,
            newsContext
          );

          const savedAnalysis = await storage.createAnalysis({
            stockId: stock.id,
            personaId: persona.id,
            recommendation: analysis.recommendation,
            confidenceScore: analysis.confidenceScore,
            reasoning: analysis.reasoning,
            targetPrice: analysis.targetPrice?.toString()
          });

          analyses.push({ persona, analysis: savedAnalysis });
        } catch (error) {
          console.error(`Failed to analyze with ${persona.name}:`, error);
        }
      }

      // Generate consensus
      const consensusResult = await openaiService.generateConsensus(
        stock,
        analyses.map(({ persona, analysis }) => ({
          persona,
          analysis: {
            recommendation: analysis.recommendation as any,
            confidenceScore: analysis.confidenceScore,
            reasoning: analysis.reasoning,
            targetPrice: analysis.targetPrice ? parseFloat(analysis.targetPrice) : undefined
          }
        }))
      );

      res.json({
        stock,
        analyses,
        consensus: consensusResult
      });
    } catch (error) {
      console.error("Analysis error:", error);
      res.status(500).json({ message: "Failed to analyze stock" });
    }
  });

  app.get("/api/stocks/:symbol/analyses", async (req, res) => {
    try {
      const { symbol } = req.params;
      const stock = await storage.getStockBySymbol(symbol.toUpperCase());

      if (!stock) {
        return res.status(404).json({ message: "Stock not found" });
      }

      const analyses = await storage.getAnalysesByStock(stock.id);
      const enrichedAnalyses = [];

      for (const analysis of analyses) {
        const persona = await storage.getPersona(analysis.personaId);
        if (persona) {
          enrichedAnalyses.push({
            ...analysis,
            persona
          });
        }
      }

      res.json(enrichedAnalyses);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch analyses" });
    }
  });

  app.get("/api/analyses/latest", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 10;
      const analyses = await storage.getLatestAnalyses(limit);
      res.json(analyses);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch latest analyses" });
    }
  });

  // Portfolio endpoints
  app.get("/api/portfolio", async (req, res) => {
    try {
      const positions = await storage.getPortfolioPositions();

      // Calculate portfolio summary
      let totalValue = 0;
      let totalReturn = 0;
      let totalReturnPercent = 0;
      let totalConsensusScore = 0;

      for (const position of positions) {
        const currentValue = parseFloat(position.currentValue || "0");
        const returnAmount = parseFloat(position.totalReturn || "0");
        const returnPercent = parseFloat(position.returnPercent || "0");
        const consensusScore = position.consensusScore || 0;

        totalValue += currentValue;
        totalReturn += returnAmount;
        totalReturnPercent += returnPercent;
        totalConsensusScore += consensusScore;
      }

      if (positions.length > 0) {
        totalReturnPercent = totalReturnPercent / positions.length;
        totalConsensusScore = totalConsensusScore / positions.length;
      }

      res.json({
        positions,
        summary: {
          totalValue,
          totalReturn,
          totalReturnPercent,
          consensusScore: totalConsensusScore
        }
      });
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch portfolio" });
    }
  });

  app.post("/api/portfolio", async (req, res) => {
    try {
      console.log("Portfolio POST request body:", req.body);
      const { symbol, shares, price } = req.body;

      console.log("Extracted values:", { symbol, shares, price });

      if (!symbol || !shares || !price) {
        console.log("Validation failed - missing fields");
        return res.status(400).json({ message: "Symbol, shares, and price are required" });
      }

      // Get or create stock
      let stock = await storage.getStockBySymbol(symbol.toUpperCase());
      if (!stock) {
        const stockData = await financialDataService.getStockQuote(symbol);
        stock = await storage.createStock({
          symbol: stockData.symbol,
          name: stockData.name,
          currentPrice: stockData.price.toString(),
          priceChange: stockData.change.toString(),
          priceChangePercent: stockData.changePercent.toString(),
          marketData: stockData
        });
      }

      // Calculate current value and return
      const currentPrice = parseFloat(stock.currentPrice || "0");
      const sharesNum = parseFloat(shares);
      const avgPrice = parseFloat(price);
      const currentValue = currentPrice * sharesNum;
      const totalReturn = currentValue - (avgPrice * sharesNum);
      const returnPercent = avgPrice > 0 ? ((currentPrice - avgPrice) / avgPrice) * 100 : 0;

      const position = await storage.createPortfolioPosition({
        stockId: stock.id,
        shares: shares.toString(),
        averagePrice: price.toString(),
        currentValue: currentValue.toString(),
        totalReturn: totalReturn.toString(),
        returnPercent: returnPercent.toString(),
        consensusScore: 75 // Default consensus score
      });

      res.json(position);
    } catch (error) {
      console.error("Portfolio error:", error);
      res.status(500).json({ message: "Failed to add to portfolio" });
    }
  });

  // Debates endpoints
  app.get("/api/debates", async (req, res) => {
    try {
      const debates = await storage.getDebates();
      res.json(debates);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch debates" });
    }
  });

  app.get("/api/stocks/:symbol/debates", async (req, res) => {
    try {
      const { symbol } = req.params;
      const stock = await storage.getStockBySymbol(symbol.toUpperCase());

      if (!stock) {
        return res.status(404).json({ message: "Stock not found" });
      }

      const debates = await storage.getDebatesByStock(stock.id);
      res.json(debates);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch debates" });
    }
  });

  app.post("/api/debates", async (req, res) => {
    try {
      const validation = insertDebateSchema.safeParse(req.body);
      if (!validation.success) {
        return res.status(400).json({ message: "Invalid debate data" });
      }

      const debate = await storage.createDebate(validation.data);
      res.json(debate);
    } catch (error) {
      res.status(500).json({ message: "Failed to create debate" });
    }
  });

  // News endpoints
  app.get("/api/news", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 20;
      const articles = await newsService.getMarketNews(limit);

      // Store in database for persistence
      for (const article of articles) {
        try {
          await storage.createNewsArticle({
            title: article.title,
            description: article.description,
            url: article.url,
            imageUrl: article.urlToImage,
            source: article.source.name,
            publishedAt: new Date(article.publishedAt),
            stockSymbols: [], // Would need NLP to extract stock symbols
            sentiment: "NEUTRAL",
            impact: "MEDIUM"
          });
        } catch (error) {
          // Article might already exist, ignore
        }
      }

      res.json(articles);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch news" });
    }
  });

  app.get("/api/news/stocks/:symbol", async (req, res) => {
    try {
      const { symbol } = req.params;
      const articles = await newsService.getStockNews(symbol);
      res.json(articles);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch stock news" });
    }
  });

  // Chat endpoints
  app.get("/api/chat/conversations", async (req, res) => {
    try {
      const conversations = await storage.getChatConversations();
      res.json(conversations);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch conversations" });
    }
  });

  app.post("/api/chat/conversations", async (req, res) => {
    try {
      const validation = insertChatConversationSchema.safeParse(req.body);
      if (!validation.success) {
        return res.status(400).json({ message: "Invalid conversation data" });
      }

      const conversation = await storage.createChatConversation(validation.data);
      res.json(conversation);
    } catch (error) {
      res.status(500).json({ message: "Failed to create conversation" });
    }
  });

  app.get("/api/chat/conversations/:id/messages", async (req, res) => {
    try {
      const { id } = req.params;
      const messages = await storage.getChatMessages(id);
      res.json(messages);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch messages" });
    }
  });

  app.post("/api/chat/conversations/:id/messages", async (req, res) => {
    try {
      const { id } = req.params;
      const messageData = { ...req.body, conversationId: id };

      const validation = insertChatMessageSchema.safeParse(messageData);
      if (!validation.success) {
        return res.status(400).json({ message: "Invalid message data" });
      }

      // Save user message
      const userMessage = await storage.createChatMessage(validation.data);

      // Generate AI consensus response
      if (validation.data.role === "user") {
        try {
          // Get current portfolio for context
          const portfolio = await storage.getPortfolioPositions();
          const portfolioContext = portfolio.map(pos => ({
            symbol: pos.stock.symbol,
            name: pos.stock.name,
            shares: pos.shares,
            avgPrice: pos.averagePrice,
            currentValue: pos.currentValue,
            return: pos.returnPercent
          }));

          // Get AI personas for consensus response
          const personas = await storage.getPersonas();

          const aiResponse = await openaiService.generateConsensusChat(
            validation.data.content,
            portfolioContext,
            personas
          );

          // Save AI response
          const aiMessage = await storage.createChatMessage({
            conversationId: id,
            role: "assistant",
            content: aiResponse.response,
            metadata: { consensusScore: aiResponse.consensusScore, personas: personas.map(p => p.name) }
          });

          res.json({ userMessage, aiMessage });
        } catch (aiError) {
          console.error("AI response failed:", aiError);
          // Return just user message if AI fails
          res.json({ userMessage, aiMessage: null });
        }
      } else {
        res.json({ userMessage });
      }
    } catch (error) {
      console.error("Chat message error:", error);
      res.status(500).json({ message: "Failed to send message" });
    }
  });

  // Portfolio upload endpoints
  app.post("/api/portfolio/upload", async (req, res) => {
    try {
      const objectStorageService = new ObjectStorageService();
      const uploadURL = await objectStorageService.getObjectEntityUploadURL();
      res.json({ uploadURL });
    } catch (error) {
      res.status(500).json({ message: "Failed to get upload URL" });
    }
  });

  app.post("/api/portfolio/process-upload", async (req, res) => {
    try {
      const { fileName, fileUrl } = req.body;

      if (!fileName || !fileUrl) {
        return res.status(400).json({ message: "fileName and fileUrl are required" });
      }

      // Create portfolio upload record
      const upload = await storage.createPortfolioUpload({
        fileName,
        fileUrl,
        status: "PROCESSING"
      });

      // TODO: Process CSV/Excel file and extract positions
      // For now, return the upload record
      res.json(upload);
    } catch (error) {
      res.status(500).json({ message: "Failed to process upload" });
    }
  });

  // Object storage endpoint for serving uploaded files
  app.get("/objects/:objectPath(*)", async (req, res) => {
    const objectStorageService = new ObjectStorageService();
    try {
      const objectFile = await objectStorageService.getObjectEntityFile(req.path);
      objectStorageService.downloadObject(objectFile, res);
    } catch (error) {
      console.error("Error accessing object:", error);
      if (error instanceof ObjectNotFoundError) {
        return res.sendStatus(404);
      }
      return res.sendStatus(500);
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type InvestorPersona, 
  type InsertInvestorPersona,
  type Stock,
  type InsertStock,
  type StockAnalysis,
  type InsertStockAnalysis,
  type Debate,
  type InsertDebate,
  type PortfolioPosition,
  type InsertPortfolioPosition,
  type NewsArticle,
  type InsertNewsArticle,
  type ChatConversation,
  type InsertChatConversation,
  type ChatMessage,
  type InsertChatMessage,
  type PortfolioUpload,
  type InsertPortfolioUpload,
  investorPersonas,
  stocks,
  stockAnalyses,
  debates,
  portfolioPositions,
  newsArticles,
  chatConversations,
  chatMessages,
  portfolioUploads
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, sql } from "drizzle-orm";

export interface IStorage {
  // Personas
  getPersonas(): Promise<InvestorPersona[]>;
  getPersona(id: string): Promise<InvestorPersona | undefined>;
  createPersona(persona: InsertInvestorPersona): Promise<InvestorPersona>;

  // Stocks
  getStocks(): Promise<Stock[]>;
  getStock(id: string): Promise<Stock | undefined>;
  getStockBySymbol(symbol: string): Promise<Stock | undefined>;
  createStock(stock: InsertStock): Promise<Stock>;
  updateStock(id: string, updates: Partial<Stock>): Promise<Stock | undefined>;

  // Stock Analyses
  getAnalysesByStock(stockId: string): Promise<StockAnalysis[]>;
  getAnalysisByStockAndPersona(stockId: string, personaId: string): Promise<StockAnalysis | undefined>;
  createAnalysis(analysis: InsertStockAnalysis): Promise<StockAnalysis>;
  getLatestAnalyses(limit?: number): Promise<(StockAnalysis & { stock: Stock; persona: InvestorPersona })[]>;

  // Debates
  getDebates(): Promise<Debate[]>;
  getDebate(id: string): Promise<Debate | undefined>;
  getDebatesByStock(stockId: string): Promise<Debate[]>;
  createDebate(debate: InsertDebate): Promise<Debate>;
  updateDebate(id: string, updates: Partial<Debate>): Promise<Debate | undefined>;

  // Portfolio
  getPortfolioPositions(): Promise<(PortfolioPosition & { stock: Stock })[]>;
  getPortfolioPosition(id: string): Promise<PortfolioPosition | undefined>;
  getPortfolioPositionByStock(stockId: string): Promise<PortfolioPosition | undefined>;
  createPortfolioPosition(position: InsertPortfolioPosition): Promise<PortfolioPosition>;
  updatePortfolioPosition(id: string, updates: Partial<PortfolioPosition>): Promise<PortfolioPosition | undefined>;
  deletePortfolioPosition(id: string): Promise<boolean>;

  // News
  getNewsArticles(limit?: number): Promise<NewsArticle[]>;
  getNewsArticlesByStock(symbols: string[]): Promise<NewsArticle[]>;
  createNewsArticle(article: InsertNewsArticle): Promise<NewsArticle>;

  // Chat
  getChatConversations(): Promise<ChatConversation[]>;
  getChatConversation(id: string): Promise<ChatConversation | undefined>;
  createChatConversation(conversation: InsertChatConversation): Promise<ChatConversation>;
  getChatMessages(conversationId: string): Promise<ChatMessage[]>;
  createChatMessage(message: InsertChatMessage): Promise<ChatMessage>;

  // Portfolio Uploads
  getPortfolioUploads(): Promise<PortfolioUpload[]>;
  getPortfolioUpload(id: string): Promise<PortfolioUpload | undefined>;
  createPortfolioUpload(upload: InsertPortfolioUpload): Promise<PortfolioUpload>;
  updatePortfolioUpload(id: string, updates: Partial<PortfolioUpload>): Promise<PortfolioUpload | undefined>;
}

export class DatabaseStorage implements IStorage {
  constructor() {
    this.initializePersonas();
  }

  private async initializePersonas() {
    try {
      const existingPersonas = await db.select().from(investorPersonas).limit(1);
      if (existingPersonas.length > 0) return;

      const defaultPersonas: InsertInvestorPersona[] = [
        {
          name: "Warren Buffett",
          description: "The Oracle of Omaha - Value investing legend focused on long-term intrinsic value",
          avatar: "üßô‚Äç‚ôÇÔ∏è",
          investmentStyle: "Value",
          personalityTraits: ["patient", "analytical", "conservative", "long-term focused"]
        },
        {
          name: "Cathie Wood",
          description: "Innovation investor focused on disruptive technology and exponential growth",
          avatar: "üöÄ",
          investmentStyle: "Growth/Innovation", 
          personalityTraits: ["visionary", "risk-taking", "tech-focused", "disruptive"]
        },
        {
          name: "Peter Lynch",
          description: "Growth at a reasonable price (GARP) investor with focus on understandable businesses",
          avatar: "üìä",
          investmentStyle: "GARP",
          personalityTraits: ["practical", "research-driven", "opportunistic", "retail-focused"]
        },
        {
          name: "Michael Burry",
          description: "Contrarian value investor known for contrarian bets and deep fundamental analysis",
          avatar: "üïµÔ∏è",
          investmentStyle: "Deep Value/Contrarian",
          personalityTraits: ["contrarian", "analytical", "skeptical", "independent"]
        },
        {
          name: "Bill Ackman",
          description: "Activist investor focused on high-conviction concentrated positions",
          avatar: "‚ö°",
          investmentStyle: "Activist/Concentrated",
          personalityTraits: ["activist", "high-conviction", "concentrated", "outspoken"]
        }
      ];

      await db.insert(investorPersonas).values(defaultPersonas);
    } catch (error) {
      console.log("Personas might already exist or database not ready:", error);
    }
  }

  // Personas
  async getPersonas(): Promise<InvestorPersona[]> {
    return await db.select().from(investorPersonas);
  }

  async getPersona(id: string): Promise<InvestorPersona | undefined> {
    const [persona] = await db.select().from(investorPersonas).where(eq(investorPersonas.id, id));
    return persona || undefined;
  }

  async createPersona(persona: InsertInvestorPersona): Promise<InvestorPersona> {
    const [created] = await db.insert(investorPersonas).values(persona).returning();
    return created;
  }

  // Stocks
  async getStocks(): Promise<Stock[]> {
    return await db.select().from(stocks);
  }

  async getStock(id: string): Promise<Stock | undefined> {
    const [stock] = await db.select().from(stocks).where(eq(stocks.id, id));
    return stock || undefined;
  }

  async getStockBySymbol(symbol: string): Promise<Stock | undefined> {
    const [stock] = await db.select().from(stocks).where(eq(stocks.symbol, symbol));
    return stock || undefined;
  }

  async createStock(stock: InsertStock): Promise<Stock> {
    const [created] = await db.insert(stocks).values(stock).returning();
    return created;
  }

  async updateStock(id: string, updates: Partial<Stock>): Promise<Stock | undefined> {
    const [updated] = await db.update(stocks).set(updates).where(eq(stocks.id, id)).returning();
    return updated || undefined;
  }

  // Stock Analyses
  async getAnalysesByStock(stockId: string): Promise<StockAnalysis[]> {
    return await db.select().from(stockAnalyses).where(eq(stockAnalyses.stockId, stockId));
  }

  async getAnalysisByStockAndPersona(stockId: string, personaId: string): Promise<StockAnalysis | undefined> {
    const [analysis] = await db.select().from(stockAnalyses)
      .where(sql`${stockAnalyses.stockId} = ${stockId} AND ${stockAnalyses.personaId} = ${personaId}`);
    return analysis || undefined;
  }

  async createAnalysis(analysis: InsertStockAnalysis): Promise<StockAnalysis> {
    const [created] = await db.insert(stockAnalyses).values(analysis).returning();
    return created;
  }

  async getLatestAnalyses(limit = 10): Promise<(StockAnalysis & { stock: Stock; persona: InvestorPersona })[]> {
    const results = await db.select({
      id: stockAnalyses.id,
      stockId: stockAnalyses.stockId,
      personaId: stockAnalyses.personaId,
      recommendation: stockAnalyses.recommendation,
      confidenceScore: stockAnalyses.confidenceScore,
      reasoning: stockAnalyses.reasoning,
      targetPrice: stockAnalyses.targetPrice,
      analysisDate: stockAnalyses.analysisDate,
      stock: stocks,
      persona: investorPersonas
    })
    .from(stockAnalyses)
    .leftJoin(stocks, eq(stockAnalyses.stockId, stocks.id))
    .leftJoin(investorPersonas, eq(stockAnalyses.personaId, investorPersonas.id))
    .orderBy(desc(stockAnalyses.analysisDate))
    .limit(limit);

    return results.map(result => ({
      ...result,
      stock: result.stock!,
      persona: result.persona!
    }));
  }

  // Debates
  async getDebates(): Promise<Debate[]> {
    return await db.select().from(debates).orderBy(desc(debates.createdAt));
  }

  async getDebate(id: string): Promise<Debate | undefined> {
    const [debate] = await db.select().from(debates).where(eq(debates.id, id));
    return debate || undefined;
  }

  async getDebatesByStock(stockId: string): Promise<Debate[]> {
    return await db.select().from(debates).where(eq(debates.stockId, stockId)).orderBy(desc(debates.createdAt));
  }

  async createDebate(debate: InsertDebate): Promise<Debate> {
    const [created] = await db.insert(debates).values(debate).returning();
    return created;
  }

  async updateDebate(id: string, updates: Partial<Debate>): Promise<Debate | undefined> {
    const [updated] = await db.update(debates).set(updates).where(eq(debates.id, id)).returning();
    return updated || undefined;
  }

  // Portfolio
  async getPortfolioPositions(): Promise<(PortfolioPosition & { stock: Stock })[]> {
    const results = await db.select({
      id: portfolioPositions.id,
      stockId: portfolioPositions.stockId,
      shares: portfolioPositions.shares,
      averagePrice: portfolioPositions.averagePrice,
      currentValue: portfolioPositions.currentValue,
      totalReturn: portfolioPositions.totalReturn,
      returnPercent: portfolioPositions.returnPercent,
      consensusScore: portfolioPositions.consensusScore,
      addedAt: portfolioPositions.addedAt,
      updatedAt: portfolioPositions.updatedAt,
      stock: stocks
    })
    .from(portfolioPositions)
    .leftJoin(stocks, eq(portfolioPositions.stockId, stocks.id))
    .orderBy(desc(portfolioPositions.addedAt));

    return results.map(result => ({
      ...result,
      stock: result.stock!
    }));
  }

  async getPortfolioPosition(id: string): Promise<PortfolioPosition | undefined> {
    const [position] = await db.select().from(portfolioPositions).where(eq(portfolioPositions.id, id));
    return position || undefined;
  }

  async getPortfolioPositionByStock(stockId: string): Promise<PortfolioPosition | undefined> {
    const [position] = await db.select().from(portfolioPositions).where(eq(portfolioPositions.stockId, stockId));
    return position || undefined;
  }

  async createPortfolioPosition(position: InsertPortfolioPosition): Promise<PortfolioPosition> {
    const [created] = await db.insert(portfolioPositions).values(position).returning();
    return created;
  }

  async updatePortfolioPosition(id: string, updates: Partial<PortfolioPosition>): Promise<PortfolioPosition | undefined> {
    const [updated] = await db.update(portfolioPositions).set(updates).where(eq(portfolioPositions.id, id)).returning();
    return updated || undefined;
  }

  async deletePortfolioPosition(id: string): Promise<boolean> {
    const result = await db.delete(portfolioPositions).where(eq(portfolioPositions.id, id));
    return result.rowCount > 0;
  }

  // News
  async getNewsArticles(limit = 20): Promise<NewsArticle[]> {
    return await db.select().from(newsArticles).orderBy(desc(newsArticles.publishedAt)).limit(limit);
  }

  async getNewsArticlesByStock(symbols: string[]): Promise<NewsArticle[]> {
    return await db.select().from(newsArticles)
      .where(sql`${newsArticles.stockSymbols} && ${symbols}`)
      .orderBy(desc(newsArticles.publishedAt));
  }

  async createNewsArticle(article: InsertNewsArticle): Promise<NewsArticle> {
    const [created] = await db.insert(newsArticles).values(article).returning();
    return created;
  }

  // Chat
  async getChatConversations(): Promise<ChatConversation[]> {
    return await db.select().from(chatConversations).orderBy(desc(chatConversations.updatedAt));
  }

  async getChatConversation(id: string): Promise<ChatConversation | undefined> {
    const [conversation] = await db.select().from(chatConversations).where(eq(chatConversations.id, id));
    return conversation || undefined;
  }

  async createChatConversation(conversation: InsertChatConversation): Promise<ChatConversation> {
    const [created] = await db.insert(chatConversations).values(conversation).returning();
    return created;
  }

  async getChatMessages(conversationId: string): Promise<ChatMessage[]> {
    return await db.select().from(chatMessages)
      .where(eq(chatMessages.conversationId, conversationId))
      .orderBy(chatMessages.createdAt);
  }

  async createChatMessage(message: InsertChatMessage): Promise<ChatMessage> {
    const [created] = await db.insert(chatMessages).values(message).returning();
    return created;
  }

  // Portfolio Uploads
  async getPortfolioUploads(): Promise<PortfolioUpload[]> {
    return await db.select().from(portfolioUploads).orderBy(desc(portfolioUploads.uploadedAt));
  }

  async getPortfolioUpload(id: string): Promise<PortfolioUpload | undefined> {
    const [upload] = await db.select().from(portfolioUploads).where(eq(portfolioUploads.id, id));
    return upload || undefined;
  }

  async createPortfolioUpload(upload: InsertPortfolioUpload): Promise<PortfolioUpload> {
    const [created] = await db.insert(portfolioUploads).values(upload).returning();
    return created;
  }

  async updatePortfolioUpload(id: string, updates: Partial<PortfolioUpload>): Promise<PortfolioUpload | undefined> {
    const [updated] = await db.update(portfolioUploads).set(updates).where(eq(portfolioUploads.id, id)).returning();
    return updated || undefined;
  }
}

export const storage = new DatabaseStorage();

