                <div className="h-3 bg-muted rounded mb-1"></div>
                <div className="h-3 bg-muted rounded w-8"></div>
              </div>
            </div>
          ))}
        </div>
      </section>
    );
  }

  // Mock scores for display (in real app, these would come from recent analyses)
  const personaScores: Record<string, number> = {
    "Warren Buffett": 92,
    "Cathie Wood": 88,
    "Peter Lynch": 65,
    "Michael Burry": 34,
    "Bill Ackman": 78
