  );
}

client/src/components/stock-search.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Search, TrendingUp, TrendingDown, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";

interface StockSearchProps {
  onStockSelect?: (stock: any) => void;
  showAnalyzeButton?: boolean;
}

export default function StockSearch({ onStockSelect, showAnalyzeButton = true }: StockSearchProps) {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedStock, setSelectedStock] = useState<any>(null);
  const { toast } = useToast();

  const { data: searchResults = [], isLoading: searchLoading } = useQuery({
    queryKey: ['/api/stocks/search', { q: searchQuery }],
    enabled: searchQuery.length > 0,
  });

  const analyzeStockMutation = useMutation({
    mutationFn: async (symbol: string) => {
      const response = await apiRequest('POST', `/api/stocks/${symbol}/analyze`);
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Analysis Complete",
        description: `AI analysis for ${data.stock.symbol} has been generated.`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/analyses/latest'] });
    },
    onError: (error: any) => {
      toast({
        title: "Analysis Failed",
        description: error.message || "Failed to analyze stock",
        variant: "destructive",
      });
    },
  });

  const handleStockSelect = (stock: any) => {
    setSelectedStock(stock);
    setSearchQuery("");
    if (onStockSelect) {
      onStockSelect(stock);
    }
  };

  const handleAnalyze = async (symbol: string) => {
    if (!symbol) return;
    analyzeStockMutation.mutate(symbol);
  };

  return (
    <div className="space-y-4">
      {/* Search Input */}
      <div className="relative">
        <Input
          type="text"
          placeholder="Search stocks (e.g., AAPL, TSLA, NVDA)"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="pl-10 bg-card border-border text-foreground placeholder-muted-foreground focus:ring-2 focus:ring-primary focus:border-transparent"
          data-testid="input-stock-search"
        />
        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
      </div>

      {/* Search Results */}
      {searchQuery && (
        <Card className="bg-card border-border">
          {searchLoading ? (
            <div className="p-4 flex items-center justify-center">
              <Loader2 className="w-4 h-4 animate-spin mr-2" />
              <span className="text-muted-foreground">Searching stocks...</span>
            </div>
          ) : searchResults.length > 0 ? (
            <div className="divide-y divide-border">
              {searchResults.map((stock: any) => (
                <div
                  key={stock.symbol}
                  className="p-4 hover:bg-accent cursor-pointer transition-colors touch-feedback"
                  onClick={() => handleStockSelect(stock)}
                  data-testid={`stock-result-${stock.symbol}`}
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center">
                        <span className="text-sm font-bold text-primary">{stock.symbol}</span>
                      </div>
                      <div>
                        <div className="font-medium text-foreground">{stock.name}</div>
                        <div className="text-sm text-muted-foreground">{stock.symbol}</div>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="text-sm font-medium text-foreground">
                        ${stock.price?.toFixed(2)}
                      </div>
                      <div className={`text-xs flex items-center ${
                        (stock.changePercent || 0) >= 0 ? 'text-emerald-500' : 'text-destructive'
                      }`}>
                        {(stock.changePercent || 0) >= 0 ? (
                          <TrendingUp className="w-3 h-3 mr-1" />
                        ) : (
                          <TrendingDown className="w-3 h-3 mr-1" />
                        )}
                        {(stock.changePercent || 0) >= 0 ? '+' : ''}{(stock.changePercent || 0).toFixed(2)}%
                      </div>
                    </div>
                  </div>

                  {showAnalyzeButton && (
                    <div className="mt-3 pt-3 border-t border-border">
                      <Button
                        size="sm"
                        className="w-full touch-feedback"
                        onClick={(e) => {
                          e.stopPropagation();
                          handleAnalyze(stock.symbol);
                        }}
                        disabled={analyzeStockMutation.isPending}
                        data-testid={`button-analyze-${stock.symbol}`}
                      >
                        {analyzeStockMutation.isPending ? (
                          <>
                            <Loader2 className="w-4 h-4 animate-spin mr-2" />
                            Analyzing...
                          </>
                        ) : (
                          "Get AI Analysis"
                        )}
                      </Button>
                    </div>
                  )}
                </div>
              ))}
            </div>
          ) : (
            <div className="p-4 text-center text-muted-foreground">
              No stocks found for "{searchQuery}"
            </div>
          )}
        </Card>
      )}

      {/* Selected Stock */}
      {selectedStock && !searchQuery && (
        <Card className="bg-card border-border p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="w-12 h-12 bg-primary/10 rounded-lg flex items-center justify-center">
                <span className="text-lg font-bold text-primary">{selectedStock.symbol}</span>
              </div>
              <div>
                <div className="font-semibold text-foreground">{selectedStock.name}</div>
                <div className="text-sm text-muted-foreground">
                  ${selectedStock.price?.toFixed(2)}{' '}
                  <span className={`${
                    (selectedStock.changePercent || 0) >= 0 ? 'text-emerald-500' : 'text-destructive'
                  }`}>
                    {(selectedStock.changePercent || 0) >= 0 ? '+' : ''}{(selectedStock.changePercent || 0).toFixed(2)}%
                  </span>
                </div>
              </div>
            </div>
            {showAnalyzeButton && (
              <Button
                onClick={() => handleAnalyze(selectedStock.symbol)}
                disabled={analyzeStockMutation.isPending}
                className="touch-feedback"
                data-testid={`button-analyze-selected-${selectedStock.symbol}`}
              >
                {analyzeStockMutation.isPending ? (
                  <>
                    <Loader2 className="w-4 h-4 animate-spin mr-2" />
                    Analyzing...
                  </>
                ) : (
                  "Analyze"
                )}
              </Button>
            )}
          </div>
        </Card>
      )}
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-swipe.tsx.content:
import { useEffect, useRef, useState } from "react";

interface SwipeHandlers {
  onSwipeLeft?: () => void;
  onSwipeRight?: () => void;
  onSwipeUp?: () => void;
  onSwipeDown?: () => void;
}

interface SwipeOptions {
  threshold?: number; // Minimum distance for a swipe to be detected
  velocityThreshold?: number; // Minimum velocity for a swipe
  preventDefaultTouchmoveEvent?: boolean;
  delta?: number; // Minimum delta for a swipe
}

const DEFAULT_OPTIONS: Required<SwipeOptions> = {
  threshold: 50,
  velocityThreshold: 0.3,
  preventDefaultTouchmoveEvent: false,
  delta: 10,
};

export function useSwipe(
  handlers: SwipeHandlers,
  options: SwipeOptions = {}
) {
  const { threshold, velocityThreshold, preventDefaultTouchmoveEvent, delta } = {
    ...DEFAULT_OPTIONS,
    ...options,
  };

  const [touchStart, setTouchStart] = useState<{
    x: number;
    y: number;
    time: number;
  } | null>(null);

  const [touchEnd, setTouchEnd] = useState<{
    x: number;
    y: number;
    time: number;
  } | null>(null);

  const elementRef = useRef<HTMLElement>(null);

  const onTouchStart = (e: TouchEvent) => {
    setTouchEnd(null);
    setTouchStart({
      x: e.targetTouches[0].clientX,
      y: e.targetTouches[0].clientY,
      time: Date.now(),
    });
  };

  const onTouchMove = (e: TouchEvent) => {
    if (preventDefaultTouchmoveEvent) {
      e.preventDefault();
    }
  };

  const onTouchEnd = (e: TouchEvent) => {
    if (!touchStart) return;

    setTouchEnd({
      x: e.changedTouches[0].clientX,
      y: e.changedTouches[0].clientY,
      time: Date.now(),
    });
  };

  useEffect(() => {
    if (!touchStart || !touchEnd) return;

    const distanceX = touchStart.x - touchEnd.x;
    const distanceY = touchStart.y - touchEnd.y;
    const timeDelta = touchEnd.time - touchStart.time;

    const velocity = Math.sqrt(distanceX * distanceX + distanceY * distanceY) / timeDelta;

    const isHorizontalSwipe = Math.abs(distanceX) > Math.abs(distanceY);
    const isValidSwipe = 
      Math.abs(isHorizontalSwipe ? distanceX : distanceY) > threshold &&
      velocity > velocityThreshold;

    if (!isValidSwipe) return;

    if (isHorizontalSwipe) {
      if (distanceX > delta && handlers.onSwipeLeft) {
        handlers.onSwipeLeft();
      } else if (distanceX < -delta && handlers.onSwipeRight) {
        handlers.onSwipeRight();
      }
    } else {
      if (distanceY > delta && handlers.onSwipeUp) {
        handlers.onSwipeUp();
      } else if (distanceY < -delta && handlers.onSwipeDown) {
        handlers.onSwipeDown();
      }
    }
  }, [touchEnd, touchStart, handlers, threshold, velocityThreshold, delta]);

  useEffect(() => {
    const element = elementRef.current;
    if (!element) return;

    element.addEventListener("touchstart", onTouchStart, { passive: true });
    element.addEventListener("touchmove", onTouchMove, { passive: !preventDefaultTouchmoveEvent });
    element.addEventListener("touchend", onTouchEnd, { passive: true });

    return () => {
      element.removeEventListener("touchstart", onTouchStart);
      element.removeEventListener("touchmove", onTouchMove);
      element.removeEventListener("touchend", onTouchEnd);
    };
  }, [preventDefaultTouchmoveEvent]);

  return elementRef;
}

export function useSwipeableDiv(handlers: SwipeHandlers, options?: SwipeOptions) {
  const swipeRef = useSwipe(handlers, options);

  const SwipeableDiv = ({ 
    children, 
    className = "", 
    ...props 
  }: {
    children: React.ReactNode;
    className?: string;
    [key: string]: any;
  }) => (
    <div
      ref={swipeRef}
      className={className}
      {...props}
    >
      {children}
    </div>
  );

  return SwipeableDiv;
}

// Hook for pull-to-refresh functionality
export function usePullToRefresh(onRefresh: () => void | Promise<void>) {
  const [isPulling, setIsPulling] = useState(false);
  const [pullDistance, setPullDistance] = useState(0);
  const elementRef = useRef<HTMLElement>(null);

  useEffect(() => {
    const element = elementRef.current;
    if (!element) return;

    let startY = 0;
    let isPullingDown = false;

    const handleTouchStart = (e: TouchEvent) => {
      if (window.scrollY === 0) {
        startY = e.touches[0].clientY;
        isPullingDown = true;
      }
    };

    const handleTouchMove = (e: TouchEvent) => {
      if (!isPullingDown || window.scrollY > 0) return;

      const currentY = e.touches[0].clientY;
      const distance = currentY - startY;

      if (distance > 0) {
        setIsPulling(true);
        setPullDistance(distance);

        // Add some resistance to the pull
        const resistanceDistance = distance * 0.5;
        if (resistanceDistance > 100) {
          e.preventDefault();
        }
      }
    };

    const handleTouchEnd = async () => {
      if (isPulling && pullDistance > 100) {
        try {
          await onRefresh();
        } catch (error) {
          console.error("Refresh failed:", error);
        }
      }

      setIsPulling(false);
      setPullDistance(0);
      isPullingDown = false;
    };

    element.addEventListener("touchstart", handleTouchStart, { passive: true });
    element.addEventListener("touchmove", handleTouchMove, { passive: false });
    element.addEventListener("touchend", handleTouchEnd, { passive: true });

    return () => {
      element.removeEventListener("touchstart", handleTouchStart);
      element.removeEventListener("touchmove", handleTouchMove);
      element.removeEventListener("touchend", handleTouchEnd);
    };
  }, [onRefresh, isPulling, pullDistance]);

  return {
    elementRef,
    isPulling,
    pullDistance,
  };
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

